insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Tim', 'Roth', '1961-05-14', null, 'Tim Roth, właściwie Timothy Simon Smith – angielski aktor, producent i reżyser. Sławę przyniosły mu role w filmach Quentina Tarantino – Wściekłe psy i Pulp Fiction. Częściej niż w wielkich hollywoodzkich produkcjach można go zobaczyć w filmach niezależnych i europejskich.');
