insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Daryl', 'Hannah', '1960-12-03', null, 'Daryl Christine Hannah – amerykańska aktorka filmowa, najlepiej znana z występów w filmach: Łowca androidów, Plusk, Wall Street, Roxanne, Stalowe magnolie i Kill Bill.');
