insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Christoph', 'Waltz', '1956-10-04', null, 'Christoph Waltz – austriacki aktor, zdobywca dwóch nagród Oscara za role drugoplanowe w obrazach Bękarty wojny i Django, laureat Złotej Palmy dla najlepszego aktora podczas 62. Festiwalu Filmowego w Cannes w 2009 roku oraz Złotego Globu za najlepszą rolę drugoplanową.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Eli', 'Roth', '1972-04-18', null, 'Eli Raphael Roth – amerykański scenarzysta, reżyser, aktor i producent filmowy żydowskiego pochodzenia. Jego najbardziej znane filmy to Śmiertelna gorączka, Hostel, Hostel 2. W 2007 roku wyreżyserował fikcyjny trailer do filmu Grindhouse o nazwie Thanksgiving.');
