insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Walton', 'Goggins', '1971-11-10', null, 'Walton Sanders Goggins, Jr. – amerykański aktor filmowy i telewizyjny, rzadziej producent filmowy. W jego dorobku dominują głównie role oficerów policji. Wystąpił m.in. w takich filmach jak Karate Kid IV: Mistrz i uczennica, Django i Nienawistna ósemka. W prywatnym życiu jest fotografem i podróżnikiem.');
