insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Bruce', 'Dern', '1936-06-04', null, 'Bruce MacLeish Dern − amerykański aktor filmowy i telewizyjny, dwukrotnie nominowany do Oscara za rolę drugoplanową w filmie Powrót do domu z 1978 oraz za role pierwszoplanową w filmie Nebraska z 2013 roku. Trzykrotnie nominowany do nagrody Emmy i raz do BAFTA. Laureat Złotej Palmy na 67.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Joseph', 'Gordon-Levitt', '1981-02-17', null, 'Joseph Leonard Gordon-Levitt – amerykański aktor filmowy i telewizyjny.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Ellen', 'Page', '1987-02-21', null, 'Ellen Philpotts-Page – kanadyjska aktorka, znana przede wszystkim z udziału w filmach: Pułapka, Juno, Incepcja i Zakochani w Rzymie. Jest laureatką nagród Genie i ACTRA Maritimes Award, nominowana do Oscara za rolę pierwszoplanową w filmie Juno.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Tom', 'Hardy', '1977-09-15', null, 'Edward Thomas Hardy, CBE – brytyjski aktor filmowy i producent.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Ken', 'Watanabe', '1959-10-21', null, 'Ken Watanabe – japoński aktor teatralny, filmowy oraz telewizyjny. Poza Japonią znany jest przede wszystkim z ról: generała Tadamichiego Kuribayashi w Listach z Iwo Jimy oraz Katsumoto w filmie Ostatni samuraj. Za tę drugą rolę został nominowany do Oscara.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Dileep', 'Rao', '1973-07-29', null, 'Dileep A. Rao – amerykański aktor, który pojawił się w wielu filmach fabularnych oraz serialach telewizyjnych. Znany dzięki filmom: Martwe zło, Wrota do piekieł, Avatar oraz thrillerze Christophera Nolana Incepcja.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Cillian', 'Murphy', '1976-05-25', null, 'Cillian Murphy – irlandzki aktor filmowy i teatralny.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Matt', 'Damon', '1970-10-08', null, 'Matthew Paige Damon − amerykański aktor, scenarzysta, producent filmowy i filantrop. Laureat Oscara wraz z Benem Affleckiem za najlepszy scenariusz oryginalny do filmu Buntownik z wyboru.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Jack', 'Nicholson', '1937-04-22', null, 'John Joseph „Jack” Nicholson – amerykański aktor, reżyser, scenarzysta i producent filmowy. Dwanaście razy nominowany do Oscara, trzykrotnie zdobył statuetkę. Powszechnie uznawany za jednego z najlepszych aktorów w historii.');
