insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Martin', 'Sheen', '1940-08-03', null, 'Martin Sheen, właśc. Ramón Gerard Antonio Estévez – amerykański aktor filmowy i telewizyjny, działacz pacyfistyczny pochodzenia irlandzkiego i hiszpańskiego, występował w roli głównej w filmie Czas apokalipsy i jako prezydent w serialu Prezydencki poker.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Vera', 'Farmiga', '1973-08-06', null, 'Vera Ann Farmiga, ros. Ве́ра Фарми́га, ukr. Віра Фарміґа, – amerykańska aktorka, nominowana w 2010 roku do Oscara za rolę drugoplanową w obrazie W chmurach.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Russell', 'Crowe', '1964-04-07', null, 'Russell Crowe – australijski aktor filmowy pochodzenia nowozelandzkiego. W wieku 4 lat przeprowadził się z rodzicami do Australii. Zaczął występować w australijskiej telewizji mając kilka lat. Jednak pierwszą rolą, która przyniosła mu uznanie była rola neonazisty w filmie Romper Stomper.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Joaquin', 'Phoenix', '1974-10-28', null, 'Joaquin Raphael Phoenix – amerykański aktor, wokalista, producent filmowy i telewizyjny. Laureat Złotego Globu za rolę w filmie Spacer po linie.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Connie', 'Nielsen' ,'1965-07-03', null, 'Connie Nielsen – duńska aktorka. Swoją karierę aktorską rozpoczęła, mając 15 lat. Zna biegle siedem języków. Jest rozwiedziona, ma 2 synów Sebastiana i Bryce\'a Thadeusa Ulrich-Nielsen. Od 2003 do 2012 roku była w związku z perkusistą grupy Metallica Larsem Ulrichem.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Oliver', 'Reed', '1938-02-13', '1999-05-02', 'Robert Oliver Reed − angielski aktor filmowy i telewizyjny.');
