insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Richard', 'Harris', '1930-10-01', '2002-10-25', 'Richard St. John Harris – irlandzki aktor, pieśniarz, poeta, producent, reżyser i scenarzysta.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Tom', 'Cruise', '1962-07-03', null, 'Tom Cruise, właśc. Thomas Cruise Mapother IV – amerykański aktor i producent filmowy; scjentolog. Uznany przez magazyn „Forbes” za najbardziej wpływowego człowieka show-biznesu. Za początek wielkiej kariery aktora uważa się nominowaną do Złotego Globu rolę w filmie Ryzykowny interes z 1983.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Timothy', 'Spall', '1957-02-27', null, 'Timothy Leonard Spall – brytyjski aktor, odznaczony Orderem Imperium Brytyjskiego. Od 1981 roku jest żonaty. Ma trójkę dzieci. Ukończył Królewską Akademię Sztuki Dramatycznej w Londynie Spall znany jest m.in. z filmów Mike’a Leigh, takich jak Home sweet home, Życie jest słodkie, Sekrety i kłamstwa oraz Topsy-Turvy.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Billy', 'Connolly', '1942-11-24', null, 'William Connolly Jr. – brytyjski aktor, komik, muzyk, prezenter.');
