insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Heath', 'Ledger', '1979-04-04', '2008-01-22', 'Heath Andrew Ledger – australijski aktor i reżyser, od 1998 mieszkał w Stanach Zjednoczonych; popularność zdobył dzięki głównej roli w filmie Tajemnica Brokeback Mountain, pośmiertnie nagrodzony Nagrodą Akademii Filmowej dla najlepszego aktora drugoplanowego za rolę Jokera w filmie Mroczny rycerz.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Joely', 'Richardson', '1965-01-09', null, 'Joely Kim Richardson – angielska aktorka, dwukrotnie nominowana do Złotego Globu za rolę Julii McNamary w serialu Bez skazy. Jest córką aktorki Vanessy Redgrave oraz reżysera i scenarzysty Tony’ego Richardsona, wnuczka aktorów Michaela Redgrave\'a i Rachel Kempson, siostra aktorki Natashy Richardson.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Jason', 'Isaacs', '1963-06-06', null, 'Jason Isaacs – brytyjski aktor filmowy, teatralny i telewizyjny.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Katarzyna', 'Figura', '1962-03-22', null, 'Katarzyna Małgorzata Figura – polska aktorka filmowa, telewizyjna i teatralna, symbol seksu lat 80. i 90, laureatka Orła za główną rolę kobiecą w filmie Żurek. Swoją karierę aktorską zaczynała od epizodów w filmach Krzysztofa Kieślowskiego i Wojciecha Jerzego Hasa.');
