insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Jordana', 'Brewster', '1980-04-26', null, 'Jordana Brewster – amerykańska aktorka brazylijsko-panamskiego pochodzenia. Wystąpiła w kilku popularnych filmach kinowych, jak Teksańska masakra piłą mechaniczną: Początek czy Szybcy i wściekli. Pojawiła się również w dreszczowcu Roberta Rodrigueza Oni oraz chwalonym przez krytykę serialu The \'60s.');
