insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Shad', 'Moss', '1987-03-09', null, 'Bow Wow, właśc. Shad Gregory Moss – amerykański raper oraz aktor. Został umieszczony w Księdze Rekordów Guinnessa jako „najmłodszy raper występujący solo, który zdobył pierwsze miejsca na amerykańskich listach przebojów”.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Nathalie', 'Kelley', '1985-03-03', null, 'Nathalie Kelley – australijska aktorka pochodzenia peruwiańskiego. Urodziła się jako córka Peruwianki i Argentyńczyka. Od 1987 roku mieszka w Sydney. Ukończyła szkołę North Sydney Girls High School. Następnie studiowała politologię i stosunki międzynarodowe na Uniwersytecie Nowej Południowej Walii.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Sung', 'Kang', '1972-04-08', null, 'Sung Kang – amerykański aktor i producent pochodzenia koreańskiego. W roku 2006 zagrał w filmie The Fast And The Furious – Tokyo Drift jako Han, grał również w filmie: Znamię wojownika 2004 oraz Better Luck Tomorrow. Jest właścicielem restauracji Saketini w Brentwood, California.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Brian', 'Tee', '1977-03-15', null, 'known professionally as Brian Tee, is a Korean Japanese American actor best known for his role as Kazu in the Nickelodeon Show \'Zoey 101\' and as Dr. Ethan Choi in the NBC medical drama Chicago Med (2015–present).');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('John', 'Ortiz', '1968-05-23', null, 'John Ortiz (born May 23, 1968) is an American actor and artistic director/co-founder of the LAByrinth Theater Company. He is known for his antagonist role as Arturo Braga in Fast & Furious (2009) and Fast & Furious 6 (2013), and Clyde in Jack Goes Boating (2010), which earned him a nomination for the Independent Spirit Award for Best Supporting Actor.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Laz', 'Alonso', '1974-03-25', null, 'Laz Alonso – amerykański aktor filmowy i telewizyjny. Karierę rozpoczął w 2000 roku od niewielkiej roli w serialu TV - A.M. @ BET. Znany głównie z roli Fenixa \'Rise\'a\' Calderon w filmie Szybko i wściekle i Tsu\'teya w filmie Avatar.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Dwayne', 'Johnson', '1972-05-02', null, 'Dwayne Douglas Johnson – amerykański wrestler, aktor i futbolista samoańskiego, irlandzkiego i afrykańskiego pochodzenia, znany też pod swoim pseudonimem ringowym jako The Rock. W czasie studiów na Uniwersytecie Miami trenował futbol akademicki i wraz z reprezentacją swojej uczelni wygrał mistrzostwa krajowe w 1991.');
insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Matt', 'Schulze', '1972-07-03', null, 'Matt Schulze, właśc. Matthew Steven Schulze – amerykański aktor i reżyser filmowy i telewizyjny niemieckiego pochodzenia. Został wybrany przez magazyn GQ jako jeden z 10. najlepszych mężczyzn chętnie oglądanych w Hollywood. Występował w roli Vince’a w filmach Szybcy i wściekli i Szybcy i wściekli 5.');
