insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Kinga', 'Preis', '1971-08-31', null, 'Kinga Anna Preis – polska aktorka, pięciokrotna laureatka Orła za pierwszoplanowe role w filmach Cisza i Komornik oraz za drugoplanowe w filmach Wtorek, W ciemności i Pod Mocnym Aniołem. Zdobyła także 10 nominacji przez co stała się najczęściej nagradzaną i nominowaną aktorką w historii tejże nagrody.');
