insert into actors(`name`, surname, date_of_birth, date_of_death, summary) values ('Michał', 'Piela', '1978-12-21', null, 'Michał Piela – polski aktor filmowy, telewizyjny i dubbingowy. Występował w roli aspiranta Nocula w serialu Ojciec Mateusz. Mierzy 196 cm wzrostu. W 2003 ukończył PWST w Warszawie, ale już wcześniej pojawił się w serialu Plebania.');
insert into directors(`name`, surname, date_of_birth, date_of_death, summary) values ('Quentin', 'Tarantino', '1963-03-27', null, 'Quentin Jerome Tarantino – amerykański reżyser, scenarzysta i aktor filmowy, sporadycznie także producent, jeden z głównych przedstawicieli postmodernizmu filmowego. Karierę rozpoczął jako twórca scenariuszy do filmów Urodzeni mordercy i Prawdziwy romans.');
insert into directors(`name`, surname, date_of_birth, date_of_death, summary) values ('Christopher', 'Nolan', '1970-07-30', null, 'Christopher Edward Nolan – brytyjski reżyser, scenarzysta i producent filmowy. Jest jednym z najbardziej dochodowych reżyserów w historii, a także jednym z najbardziej cenionych filmowców XXI wieku.');
