insert into directors(`name`, surname, date_of_birth, date_of_death, summary) values ('James', 'Wam', '1977-02-27', null, 'James Wan – australijski producent, reżyser i scenarzysta filmowy pochodzenia malezyjskiego. Do jego wczesnych dzieł należą film krótkometrażowy Piła i thriller o tym samym tytule z 2004 roku, które zapoczątkowały serię krwawych filmów gore.');
insert into directors(`name`, surname, date_of_birth, date_of_death, summary) values ('F. Gary', 'Gray', '1969-07-17', null, 'Felix Gary Gray (born July 17, 1969) is an American film director, film producer, music video director and actor. Gray directed Friday, Set It Off, The Negotiator, The Italian Job and Straight Outta Compton. He also directed the eighth installment of the Fast and the Furious franchise, The Fate of the Furious, which is the 16th highest-grossing film of all-time.');
