insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Nienawistna ósemka', 'The Hateful Eight', 1, 'Dwaj łowcy głów, próbując znaleźć schronienie przed zamiecią śnieżną, trafiają do Wyoming, gdzie wplątani zostają w splot krwawych wydarzeń.', null, '2016-01-15', true, 3);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Incepcja', 'Inception', 2, 'Czasy, gdy technologia pozwala na wchodzenie w świat snów. Złodziej Cobb ma za zadanie wszczepić myśl do śpiącego umysłu.', null, '2010-07-30', true, 6);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Infiltracja', 'The Departed', 17, 'Tajny policjant w szeregach grupy przestępczej i informator mafii w bostońskiej jednostce dochodzeniowej toczą ze sobą śmiertelną rozgrywkę.', null, '2006-10-27', true, 2);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Gladiator', 'Gladiator', 3, 'Generał Maximus - prawa ręka cesarza, szczęśliwy mąż i ojciec - w jednej chwili traci wszystko. Jako niewolnik-gladiator musi walczyć na arenie o przeżycie.', null, '2000-07-14', true, 7);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Ostatni samuraj', 'The Last Samurai', 4, 'Weteran wojny secesyjnej przypływa do Japonii pod koniec 1870 roku, aby szkolić żołnierzy cesarza Mutsuhito.', null, '2004-01-30', true, 7);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Patriota', 'The Patriot', 5, 'Weteran wojny francusko-indiańskiej osiada na farmie i zakłada rodzinę. Gdy wybucha kolejny konflikt, musi zdecydować: stanąć do walki czy zaopiekować się najbliższymi.', null, '2000-07-28', true, 7);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Kiler', 'Kiler', 6, 'Jerzy Kiler, warszawski taksówkarz, przypadkowo zostaje wzięty za płatnego zabójcę i umieszczony w areszcie. Wyciąga go stamtąd boss świata przestępczego, który oferuje mu nowe zadanie.', null, '1997-11-17', true, 1);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Kiler-ów 2-óch', 'Kiler-ów 2-óch', 6, 'Jurek Kiler stał się sławną osobą, lecz gangsterzy, których oszukał, planują zemstę.', null, '1999-01-15', true, 1);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Szybcy i wściekli', 'The Fast and the Furious', 7, 'Policjant przenika do grupy organizującej nielegalne wyścigi samochodowe. Sytuacja komplikuje się, gdy poznaje bliżej siostrę lidera przestępców.', null, '2001-09-21', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Za szybcy, za wściekli', '2 Fast 2 Furious', 8, 'Jedyną szansą na schwytanie piorącego brudne pieniądze biznesmena jest infiltracja środowiska nielegalnych wyścigów samochodowych. Zadania podejmuje się były policjant.', null, '2003-06-20', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Szybcy i wściekli: Tokio Drift', 'The Fast and the Furious: Tokyo Drift', 9, 'Pasjonat nielegalnych wyścigów samochodowych ucieka przed więzieniem do Tokio. Ale tutaj też organizowane są niebezpieczne rajdy.', null, '2006-08-11', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Szybko i wściekle', 'Fast & Furious', 9, 'Przestępca oraz tropiący go agent łączą swoje siły i przenikają do narkotykowego gangu, by dopaść jego przywódcę.', null, '2009-04-03', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Szybcy i wściekli 5', 'Fast Five', 9, 'Grupa kierowców naraża się niebezpiecznemu gangsterowi, organizuje ryzykowny napad i musi wymknąć się ścigającemu ich nieustępliwemu agentowi.', null, '2011-05-06', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Szybcy i wściekli 6', 'Furious 6', 9, 'Grupa świetnie wyszkolonych kierowców porywa transport wojskowej broni. W zamian za całkowitą abolicję Dom z przyjaciółmi zgadzają się pomóc agentowi Hobbsowi przy powstrzymaniu najemników.', null, '2013-05-24', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Szybcy i wściekli 7', 'Furious Seven', 10, 'Toretto i jego ekipa mierzą się z pałającym żądzą krwi najemnikiem Deckardem Shawem.', null, '2015-04-10', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Szybcy i wściekli 8', 'The Fate of the Furious', 11, 'Szczęśliwy miesiąc miodowy Dominica i Letty przerywa pojawienie się tajemniczej kobiety, która ponownie wciąga mężczyznę w świat zbrodni.', null, '2017-04-21', true, 5);
insert into movies(title, original_title, director_id, `description`, average_score, release_date, is_released, category_id) values('Iron Man', 'Iron Man', 13, 'Tony Stark buduje supernowoczesną zbroję. Multimiliarder postanawia walczyć ze złem jako Iron Man.', null, '2008-04-30', true, 5);
